import React, { useState } from 'react';
import { signInWithEmailAndPassword, fetchSignInMethodsForEmail, confirmPasswordReset, updatePassword } from 'firebase/auth';
import { auth } from '../firebase';
import '../index.css';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [isResettingPassword, setIsResettingPassword] = useState(false);
    const [emailVerified, setEmailVerified] = useState(false);
    const [newPassword, setNewPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            await signInWithEmailAndPassword(auth, email, password);
            // Rediriger ou afficher un message de succès
        } catch (error) {
            setError(error.message);
        }
    };

    const verifyEmail = async () => {
        if (!email) {
            setError("Veuillez entrer votre adresse e-mail.");
            return;
        }
        try {
            const signInMethods = await fetchSignInMethodsForEmail(auth, email);
            if (signInMethods.length > 0) {
                setEmailVerified(true);
                setError('');
            } else {
                setError("Aucun compte trouvé pour cette adresse e-mail.");
            }
        } catch (error) {
            setError(error.message);
        }
    };

    const handlePasswordReset = async (e) => {
        e.preventDefault();
        if (newPassword !== confirmPassword) {
            setError("Les mots de passe ne correspondent pas.");
            return;
        }
        try {
            const user = auth.currentUser;
            await updatePassword(user, newPassword);
            setError('');
            // Rediriger ou afficher un message de succès
        } catch (error) {
            setError(error.message);
        }
    };

    return (
        <div className="container">
            {!isResettingPassword ? (
                <>
                    <h1>Connexion</h1>
                    <form onSubmit={handleLogin}>
                        <input
                            type="email"
                            placeholder="Email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                        />
                        <input
                            type="password"
                            placeholder="Mot de passe"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                        />
                        <button type="submit">Se connecter</button>
                        {error && <p>{error}</p>}
                    </form>
                    <a href="#!" onClick={() => setIsResettingPassword(true)}>Mot de passe oublié</a>
                </>
            ) : (
                <>
                    <h1>Réinitialiser le mot de passe</h1>
                    {!emailVerified ? (
                        <>
                            <input
                                type="email"
                                placeholder="Email"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                            />
                            <button onClick={verifyEmail}>Vérifier l'Email</button>
                            {error && <p>{error}</p>}
                        </>
                    ) : (
                        <form onSubmit={handlePasswordReset}>
                            <input
                                type="password"
                                placeholder="Nouveau mot de passe"
                                value={newPassword}
                                onChange={(e) => setNewPassword(e.target.value)}
                            />
                            <input
                                type="password"
                                placeholder="Confirmer le nouveau mot de passe"
                                value={confirmPassword}
                                onChange={(e) => setConfirmPassword(e.target.value)}
                            />
                            <button type="submit">Réinitialiser le mot de passe</button>
                            {error && <p>{error}</p>}
                        </form>
                    )}
                </>
            )}
        </div>
    );
};

export default Login;
