import React, { useState } from 'react';
import { signInWithEmailAndPassword, sendPasswordResetEmail, confirmPasswordReset } from 'firebase/auth';
import { auth } from '../firebase';
import '../index.css';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [resetEmailSent, setResetEmailSent] = useState(false);
    const [isResettingPassword, setIsResettingPassword] = useState(false);
    const [newPassword, setNewPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            await signInWithEmailAndPassword(auth, email, password);
            // Rediriger ou afficher un message de succès
        } catch (error) {
            setError(error.message);
        }
    };

    const handlePasswordResetEmail = async () => {
        if (!email) {
            setError("Veuillez entrer votre adresse e-mail pour réinitialiser le mot de passe.");
            return;
        }
        try {
            await sendPasswordResetEmail(auth, email);
            setResetEmailSent(true);
            setError('');
        } catch (error) {
            setError(error.message);
        }
    };

    const handlePasswordReset = async (e) => {
        e.preventDefault();
        if (newPassword !== confirmPassword) {
            setError("Les mots de passe ne correspondent pas.");
            return;
        }
        try {
            const urlParams = new URLSearchParams(window.location.search);
            const oobCode = urlParams.get('oobCode');
            await confirmPasswordReset(auth, oobCode, newPassword);
            setError('');
            // Rediriger ou afficher un message de succès
        } catch (error) {
            setError(error.message);
        }
    };

    return (
        <div className="container">
            {!isResettingPassword ? (
                <>
                    <h1>Connexion</h1>
                    <form onSubmit={handleLogin}>
                        <input
                            type="email"
                            placeholder="Email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                        />
                        <input
                            type="password"
                            placeholder="Mot de passe"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                        />
                        <button type="submit">Se connecter</button>
                        {error && <p>{error}</p>}
                        {resetEmailSent && <p>Email de réinitialisation envoyé ! Vérifiez votre boîte de réception.</p>}
                    </form>
                    <a href="#!" onClick={() => setIsResettingPassword(true)}>Mot de passe oublié</a>
                </>
            ) : (
                <>
                    <h1>Réinitialiser le mot de passe</h1>
                    <form onSubmit={handlePasswordReset}>
                        <input
                            type="password"
                            placeholder="Nouveau mot de passe"
                            value={newPassword}
                            onChange={(e) => setNewPassword(e.target.value)}
                        />
                        <input
                            type="password"
                            placeholder="Confirmer le nouveau mot de passe"
                            value={confirmPassword}
                            onChange={(e) => setConfirmPassword(e.target.value)}
                        />
                        <button type="submit">Réinitialiser le mot de passe</button>
                        {error && <p>{error}</p>}
                    </form>
                </>
            )}
        </div>
    );
};

export default Login;
