import React, { useState } from 'react';
import { signInWithEmailAndPassword, sendPasswordResetEmail } from 'firebase/auth';
import { auth } from '../firebase';
import '../index.css';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [resetEmailSent, setResetEmailSent] = useState(false);

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            await signInWithEmailAndPassword(auth, email, password);
            // Rediriger ou afficher un message de succès
        } catch (error) {
            setError(error.message);
        }
    };

    const handlePasswordReset = async () => {
        if (!email) {
            setError("Veuillez entrer votre adresse e-mail pour réinitialiser le mot de passe.");
            return;
        }
        try {
            await sendPasswordResetEmail(auth, email);
            setResetEmailSent(true);
            setError('');
        } catch (error) {
            setError(error.message);
        }
    };

    return (
        <div className="container">
            <h1>Connexion</h1>
            <form onSubmit={handleLogin}>
                <input
                    type="email"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                />
                <input
                    type="password"
                    placeholder="Mot de passe"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                <button type="submit">Se connecter</button>
                {error && <p>{error}</p>}
                {resetEmailSent && <p>Email de réinitialisation envoyé ! Vérifiez votre boîte de réception.</p>}
            </form>
            <button onClick={handlePasswordReset}>Mot de passe oublié</button>
        </div>
    );
};

export default Login;
